                                   kanemfgcontactus form                                                                                                            \";//WARM Inbound Leads from Neuton         if($success==\"Success\"){             $data_to_posts = array();             $data_to_posts&#91;\'email\'] =\"dev1@funnelmaker.com\";             $data_to_posts&#91;\'auth_token\'] = \"$tocken\";             $data_to_posts&#91;\'xml\'] = \"$fullform\";              /*echo \"\";            print_r($fullform);die;*/             // Initialize cURL             $curl = curl_init();             // Set the options             curl_setopt($curl,CURLOPT_URL, $form_url);             // This sets the number of fields to post             curl_setopt($curl,CURLOPT_POST, sizeof($data_to_posts));             // This is the fields to post in the form of an array.             curl_setopt($curl,CURLOPT_POSTFIELDS, $data_to_posts);             curl_setopt($curl, CURLOPT_RETURNTRANSFER, true);             //execute the post             $resultnew= curl_exec($curl);             $xmlnew = new SimpleXMLElement($resultnew);            /*echo \"\";            print_r($xmlnew);*/             $textResult=$xmlnew-&gt;Contacts-&gt;Contact-&gt;Result;             $textResulterror=$xmlnew-&gt;Contacts-&gt;Contact-&gt;ErrorText;             $Contact_id=$xmlnew-&gt;Contacts-&gt;Contact-&gt;Contact_id;             if($textResult==\'Success\'){                 echo json_encode(array(\'status\'=&gt;200,\'msg\'=&gt;\'your registration Successfully\'));             }else{                 echo json_encode(array(\'status\'=&gt;402,\'msg\'=&gt;$textResulterror));             }         }else{            echo json_encode(array(\'status\'=&gt;403,\'msg\'=&gt;$tknerror));         }     }else{     echo json_encode(array(\'status\'=&gt;403,\'msg\'=&gt;\'You did not fill out the required fields\'));     } } function get_string_between($string, $start, $end){     $string = \' \' . $string;     $ini = strpos($string, $start);     if ($ini == 0) return \'\';     $ini += strlen($start);     $len = strpos($string, $end, $ini) - $ini;     return substr($string, $ini, $len); }\n ?&gt;\n</code></pre>\n<!-- /wp:code -->', 'Extracting Email Body and Sender Details from Gmail Using PHP (IMAP &amp; cURL)', '', 'inherit', 'closed', 'closed', '', '129-revision-v1', '', '', '2025-03-19 17:06:08', '2025-03-19 17:06:08', '', 129, 'https://codenskills.com/?p=707', 0, 'revision', '', 0);
